import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import './App.css';
import MuiThemeProvider from '@material-ui/core/styles/MuiThemeProvider';
import createMuiTheme from '@material-ui/core/styles/createMuiTheme';
import jwtDecode from 'jwt-decode';
// Redux
import { Provider } from 'react-redux';
import store from './redux/store';
import { SET_AUTHENTICATED } from './redux/types';
import { logoutUser, getUserData } from './redux/actions/userActions';
// Components
import Navbar from './components/layout/Navbar';
import themeObject from './util/theme';
import AuthRoute from './util/AuthRoute';
import { Client } from '@stomp/stompjs';
// Pages
import home from './pages/home';
import login from './pages/login';
import signup from './pages/signup';
import user from './pages/user';

import axios from 'axios';

const theme = createMuiTheme(themeObject);

axios.defaults.baseURL =
  'https://europe-west1-socialape-d081e.cloudfunctions.net/api';

const token = localStorage.FBIdToken;
if (token) {
  const decodedToken = jwtDecode(token);
  if (decodedToken.exp * 1000 < Date.now()) {
    store.dispatch(logoutUser());
    window.location.href = '/login';
  } else {
    store.dispatch({ type: SET_AUTHENTICATED });
    axios.defaults.headers.common['Authorization'] = token;
    store.dispatch(getUserData());
  }
}

class App extends Component {
  state={
    serverTime:null
  }

  componentDidMount(){
    this.client =new Client();

    this.client.configure({
      brokerURL:'ws//localhost:8080/stomp',
      onConnect:()=>{
        this.client.subscribe('/queue/now', message=>{
          this.setState({serverTime:message.body});
        });
      },
      debug:(str)=>{
        console.log(new Date,str);
      }
    });
    this.client.activate();
  }

  clickHandler = () => {
    this.client.publish({destination: '/app/greetings', body: 'Hello world'});
  }

  render() {
    return (
      <MuiThemeProvider theme={theme}>
        <Provider store={store}>
          <Router>
            <Navbar />
            <div className="container">
              <Switch>
                <Route exact path="/" component={home} />
                <AuthRoute exact path="/login" component={login} />
                <AuthRoute exact path="/signup" component={signup} />
                <Route exact path="/users/:handle" component={user} />
                <Route
                  exact
                  path="/users/:handle/scream/:screamId"
                  component={user}
                />
              </Switch>
            </div>
          </Router>
        </Provider>

        <div className="chat-container">
          <div className="chat-header">
            <h1>Messager</h1>
          </div>
          <hr/>
          <div className="connecting">...</div>
          <div className="chat-main">
            <ul className="message-area"></ul>
            <form className="messageForm">
              <div className="input-message">
                <input className="message" type="text" autoComplete="off" placeholder="Type message"/>
                <button onClick={this.clickHandler} type="submit">Send</button>
              </div>
            </form>
          </div>
        </div>
      </MuiThemeProvider>
    );
  }
}

export default App;
